// @flow

/*::
type cursorManager$Rect = {
  top: number;
  left: number;
  width: number;
  height: number;
};

type spatialNavigationCursor$EventListener = {
  type: string;
  listener: spatialNavigationCursor$EventHandler;
};

class spatialNavigationCursor$FocusUpdatedEvent extends CustomEvent {
  detail: {
    target: HTMLElement;
  };
}

type spatialNavigationCursor$EventHandler = (event: spatialNavigationCursor$FocusUpdatedEvent) => mixed;

type spatialNavigationCursor$Events = {
  FOCUS_UPDATED: 'focusUpdated';
};
*/

export default class CursorManager {
  /*::
  static Events: spatialNavigationCursor$Events;
  root_: HTMLElement;
  cursor_: HTMLElement;
  focused_: ?HTMLElement;
  freezed_: boolean;
  focusClassName_: string;
  viewHeight_: number;
  observer_: ?MutationObserver;
  eventListeners_: spatialNavigationCursor$EventListener[];
  */

  /**
   * @param {Object} props
   * @param {HTMLElement} props.root an HTML element to use as its root
   * @param {string} props.focusClassName a css class name to identify the focused element
   */
  constructor({ root, focusClassName }/*: {
    root: HTMLElement,
    focusClassName: string,
  }*/) {
    /**
     * @type {HTMLElement}
     * @access private
     */
    this.root_ = root;

    /**
     * @type {string}
     * @access private
     */
    this.focusClassName_ = focusClassName;

    /**
     * @type {HTMLElement}
     * @access private
     */
    this.cursor_ = document.createElement('div');

    /**
     * @type {HTMLElement}
     * @access private
     */
    this.focused_ = null;

    /**
     * @type {boolean}
     * @access private
     */
    this.freezed_ = false;

    /**
     * @type {string}
     * @access private
     */
    this.viewHeight_ = window.innerHeight;

    /**
     * @type {MutationObserver}
     * @access private
     */
    this.observer_ = null;

    /**
     * @type {Object[]}
     * @access private
     */
    this.eventListeners_ = [];
  }

  /**
   * Start the manager
   */
  start()/*: void*/ {
    this.styleCursor_();
    this.appendToRoot_();
    this.observeFocus_();
    this.focus();
  }

  /**
   * Resume the manager
   */
  resume()/*: void*/ {
    this.freezed_ = false;
    this.focus();
  }

  /**
   * Stop the manager
   */
  stop()/*: void*/ {
    this.removeFromRoot_();
    this.disconnectFocus_();
  }

  /**
   * Freeze the cursor
   */
  freeze() {
    this.freezed_ = true;
  }

  /**
   * Focus on the element with the cursor
   */
  focus()/*: void*/ {
    if (!this.focused_ || this.cursor_.style.display === 'none') {
      this.place();
    } else {
      this.move();
    }
  }

  /**
   * Place the cursor
   */
  place()/*: void*/ {
    const focused = this.focused_ || document.querySelector(`.${ this.focusClassName_ }`);
    if (!focused) return;

    const origTransitionDuration = this.cursor_.style.transitionDuration;
    this.cursor_.style.transitionDuration = '0';
    const r = getAbsoluteElementRect(focused);
    this.scrollIntoView_(r);
    this.resizeCursorTo_(r);
    this.moveCursorTo_(r);
    if (!focused.classList.contains(this.focusClassName_)) {
      this.hideCursor_();
    } else {
      this.showCursor_();
    }
    this.cursor_.style.transitionDuration = origTransitionDuration;
  }

  /**
   * Move the cursor
   */
  move()/*: void*/ {
    const focused = this.focused_ || document.querySelector(`.${ this.focusClassName_ }`);
    if (!focused || !focused.classList.contains(this.focusClassName_)) {
      this.hideCursor_();
      return;
    } else {
      this.showCursor_();
    }

    const r = getAbsoluteElementRect(focused);
    this.scrollIntoView_(r);
    this.resizeCursorTo_(r);
    this.moveCursorTo_(r);
  }

  /**
   * Get the cursor element
   * @return {HTMLElement} the cursor element
   */
  getCursor()/*: HTMLElement*/ {
    return this.cursor_;
  }

  /**
   * Add an event listener
   * @return {HTMLElement} the focused element
   */
  getFocusedElement()/*: ?HTMLElement*/ {
    return this.focused_;
  }

  /**
   * Add an event listener
   * @param {string} type an event type
   * @param {Function} listener an evnet handler 
   */
  addEventListener(type/*: string*/, listener/*: spatialNavigationCursor$EventHandler*/) {
    this.eventListeners_.push({ type, listener });
  }

  /**
   * Remove an event listener
   * @param {string} type an event type
   * @param {Function} listener an evnet handler 
   */
  removeEventListener(type/*: string*/, listener/*: spatialNavigationCursor$EventHandler*/) {
    const idx = this.eventListeners_.findIndex(l => l.type === type && l.listener === listener);
    if (~idx) {
      this.eventListeners_.splice(idx, 1);
    }
  }

  /**
   * Scroll into view of destination rect
   * @access private
   * @param {cursorManager$Rect} rect the destination rect
   * @param {number} rect.top
   * @param {number} rect.left
   * @param {number} rect.width
   * @param {number} rect.height
   */
  scrollIntoView_(rect/*: cursorManager$Rect*/)/*: void*/ {
    const left = window.pageXOffset;
    window.scroll({
      left,
      top: rect.top - (window.innerHeight * 0.5) + (rect.height * 0.5),
      behavior: 'smooth',
    });
  }

  /**
   * Move the cursor to the destination rect
   * @access private
   * @param {cursorManager$Rect} rect the destination rect to scroll to
   * @param {number} rect.top
   * @param {number} rect.left
   * @param {number} rect.width
   * @param {number} rect.height
   */
  moveCursorTo_(rect/*: cursorManager$Rect*/)/*: void*/ {
    const style = getComputedStyle(this.cursor_, '');
    const borserTop = parseInt(style.borderTopWidth, 10);
    const borserLeft = parseInt(style.borderLeftWidth, 10);
    this.cursor_.style.transform = `translate3d(${ rect.left - borserLeft }px, ${ rect.top - borserTop }px, 0)`;
  }

  /**
   * Move the cursor to the destination rect
   * @access private
   * @param {cursorManager$Rect} rect the destination rect to scroll to
   * @param {number} rect.top
   * @param {number} rect.left
   * @param {number} rect.width
   * @param {number} rect.height
   */
  resizeCursorTo_(rect/*: cursorManager$Rect*/)/*: void*/ {
    this.cursor_.style.width = `${ rect.width }px`;
    this.cursor_.style.height = `${ rect.height }px`;
  }

  /**
   * Style the cursor
   * @access private
   */
  styleCursor_()/*: void*/ {
    this.cursor_.classList.add('__spatial-navigation-cursor__');
    this.cursor_.style.position = 'absolute';
    this.cursor_.style.top = '0';
    this.cursor_.style.left = '0';
    this.hideCursor_();
  }

  /**
   * Hide the cursor
   * @access private
   */
  hideCursor_()/*: void*/ {
    this.cursor_.style.display = 'none';
  }

  /**
   * Show the cursor
   * @access private
   */
  showCursor_()/*: void*/ {
    this.cursor_.style.display = '';
  }

  /**
   * Append the cursor to the root
   * @access private
   */
  appendToRoot_()/*: void*/ {
    this.root_.appendChild(this.cursor_);
  }

  /**
   * Append the cursor to the root
   * @access private
   */
  removeFromRoot_()/*: void*/ {
    this.root_.removeChild(this.cursor_);
  }

  /**
   * Observe the change of focused elements
   * @access private
   */
  observeFocus_() {
    const observer = this.observer_ = new MutationObserver((mutationRecords) => {
      var target;
      for (let mutation of mutationRecords) {
        if ( (mutation.target/*: any*/).classList.contains(this.focusClassName_) ) {
          target = mutation.target;
          break;
        }
      }
      if (target) {
        this.focused_ = ((target/*: any*/)/*: HTMLElement*/);
        if (!this.freezed_) this.focus();
        this.trigger_(CursorManager.Events.FOCUS_UPDATED, new CustomEvent(CursorManager.Events.FOCUS_UPDATED, {
          detail: { target },
        }));
      } else {
        this.hideCursor_();
      }
    });
    observer.observe(this.root_, {
      attributeFilter: [ "class" ],
      attributes: true,
      subtree: true,
    });
  }

  /**
   * Stop Observering the change of focused elements
   * @access private
   */
  disconnectFocus_()/*: void*/ {
    if (!this.observer_) return;
    this.observer_.disconnect();
  }

  /**
   * Trigger an event
   * @access private
   * @param {string} type an event type
   * @param {...*} args an event type
   */
  trigger_(type/*: string*/, ...args/*: any[]*/)/*: void*/ {
    this.eventListeners_
      .filter(l => l.type === type)
      .forEach(l => l.listener(...args));
  }
}

CursorManager.Events = {
  FOCUS_UPDATED: 'focusUpdated',
};

/**
 * Get an element rect with its absolute location
 * @param {HTMLElement} elem an HTML element
 * @return {cursorManager$Rect} the element rect with its absolute location
 */
function getAbsoluteElementRect(elem/*: HTMLElement*/)/* cursorManager$Rect*/ {
  const r = elem.getBoundingClientRect();
  return { left: r.left + window.pageXOffset, top: r.top + window.pageYOffset, width: r.width, height: r.height };
}

# spatial-navigation-cursor

A simple spatial navigation cursor library

![spatial-navigation-cursor screenshot](./assets/screenshot.gif)

You can see how it works [here](https://ygoto3.github.io/spatial-navigation-cursor/demo/).

## Install

```sh
$ npm i spatial-navigation-cursor
```

## Usage

Give an element "focused" class name.  (You can use any other class name if you want.)

```html
<main>
  <div class="block focused"></div>
  <div class="block"></div>
  <div class="block"></div>
  <div class="block"></div>
  <div class="block"></div>
</main>
```

`.__spatial-navigation-cursor__` is a class selector for the cursor.  Write css code for the selector.

```css
:root {
  --transition-duration: .2s;
  --transition-easing: ease-out;
}

.__spatial-navigation-cursor__ {
  outline: 5px solid black;
  transition: width var(--transition-duration) var(--transition-easing), height var(--transition-duration) var(--transition-easing), transform var(--transition-duration) var(--transition-easing);
}
```

Instantiate a new cursor manager and start it.  When you give an element the focus class name, the cursor manager automatically moves the cursor to the element.

```js
import CursorManager from 'spatial-navigation-cursor';

const cursorManager = new CursorManager({
  root: document.body,
  focusClassName: 'focused',
});

cursorManager.start();

window.addEventListener('keydown', (e) => {
  e.preventDefault();
  switch (e.key) {
    case 'ArrowUp':
    case 'ArrowDown': {
      const focused = document.querySelector('.focused');
      if (e.key === 'ArrowUp') {
        const prev = focused.previousElementSibling;
        if (!prev) return;
        prev.classList.add('focused');
        focused.classList.remove('focused');
      } else {
        const next = focused.nextElementSibling;
        if (!next) return;
        next.classList.add('focused');
        focused.classList.remove('focused');
      }
      break;
    }
  }
});
```

## API document

API document is [here](https://ygoto3.github.io/spatial-navigation-cursor/class/spatial-navigation-cursor/index.js~CursorManager.html).

// @flow
/*global global*/
import test from 'ava';
import { JSDOM } from 'jsdom';
import td from 'testdouble';
import CursorManager from './index.js';

var observe = td.func();
var disconnect = td.func();

test.before(() => {
  global.window = new JSDOM('<body><div id="root"><p class="focused" style="position: absolute; top: 100px; left: 50px;">focused</p></div></body>').window;
  global.document = global.window.document;
  global.MutationObserver = class {
    observe() { observe() }
    disconnect() { disconnect() }
  }
  global.getComputedStyle = () => ({ borderLeftWidth: '2px', borderTopWidth: '2px' });
});

test.after(() => {
  delete global.window;
  delete global.document;
  delete global.MutationObserver;
  delete global.getComputedStyle;
});

test('cursor', t => {
  const root = document.getElementById('root');
  if (!root) {
    t.fail();
    return;
  }

  const cursorManager = new CursorManager({
    root,
    focusClassName: 'focused'
  });

  const actual = cursorManager.getCursor();
  t.is(actual.style.display, '');

  cursorManager.start();
  t.is(actual, root.querySelector('.__spatial-navigation-cursor__'));
  t.is(actual.classList.item(0), '__spatial-navigation-cursor__');
  t.is(actual.style.position, 'absolute');
  t.is(parseInt(actual.style.top, 10), 0);
  t.is(parseInt(actual.style.left, 10), 0);
  t.is(td.explain(observe).callCount, 1);

  cursorManager.stop();
  t.is(root.querySelector('.__spatial-navigation-cursor__'), null);
  t.is(td.explain(disconnect).callCount, 1);

  const listener = td.func();
  cursorManager.addEventListener(CursorManager.Events.FOCUS_UPDATED, listener);
  cursorManager.trigger_(CursorManager.Events.FOCUS_UPDATED);
  t.is(td.explain(listener).callCount, 1);

  cursorManager.removeEventListener(CursorManager.Events.FOCUS_UPDATED, listener);
  cursorManager.trigger_(CursorManager.Events.FOCUS_UPDATED);
  t.is(td.explain(listener).callCount, 1);
});
